sqW = 18
sqG = 1

printX = 0
printY = 0
def draw():
	if view == 0:
		pygame.draw.rect(win, (100, 100, 100), (0,0,1000,1000))
		printX = 0
		printY = 0
		for row in save:
			for sq in row:
				if sq == 0:
					pygame.draw.rect(win, (255, 255, 255), (printX * 20 + sqG, printY * 20 + sqG, sqW, sqW))
				if sq == 1:
					pygame.draw.rect(win, (0, 0, 0), (printX * 20 + sqG, printY * 20 + sqG, sqW, sqW))
				printX += 1
			printY += 1
			printX = 0

	if view == 1:
		pygame.draw.rect(win, (0, 0, 0), (0,0,1000,1000))
		printX = 0
		printY = 0
		for row in run:
			for sq in row:
				if sq == 0:
					pygame.draw.rect(win, (255, 255, 255), (printX * 20 + sqG, printY * 20 + sqG, sqW, sqW))
				if sq == 1:
					pygame.draw.rect(win, (0, 0, 0), (printX * 20 + sqG, printY * 20 + sqG, sqW, sqW))
				printX += 1
			printY += 1
			printX = 0

lClick = False
rClick = False

view = 0

while True:
	clock.tick(30)
	for event in pygame.event.get():
		if event.type == pygame.QUIT:
			pygame.quit()
		if event.type == pygame.MOUSEBUTTONDOWN:
			if event.button == 1:
				lClick = True
			if event.button == 3:
				rClick = True
			if event.button == 2:
				if view == 0:
					view = 1
					with open('save.txt', 'w') as data:
						for row in save:
							data.write(' '.join(map(str, row)) + '\n')
					run = save
					tick = False
				elif view == 1:
					save = []
					with open('save.txt', 'r') as data:
						for line in data:
							if len(save) < 50:
								saverow = line.split()
								saverow = [int(i) for i in saverow]
								save.append(saverow)
					view = 0
		if event.type == pygame.MOUSEBUTTONUP:
			if event.button == 1:
				lClick = False
			if event.button == 3:
				rClick = False

	if view == 0:
		mouseX, mouseY = pygame.mouse.get_pos()
		mouseX /= 20
		mouseX = int(mouseX)

		mouseY /= 20
		mouseY = int(mouseY)

		if lClick == True and rClick == False:
			save[mouseY][mouseX] = 1
		if lClick == False and rClick == True:
			save[mouseY][mouseX] = 0

	if view == 1:
		if lClick == True and rClick == False:
			tick = True
			lClick = False
		if lClick == False and rClick == True:
			tick = True

		if tick:
			printX = 0
			printY = 0
			for row in run:
				for sq in row:
					sqcount = 0
					if printX + 1 != 50:
						if (run[printY][printX + 1] == 1) or (run[printY][printX + 1] == 2):
							sqcount += 1
					if printX - 1 != -1:
						if (run[printY][printX - 1] == 1) or (run[printY][printX - 1] == 2):
							sqcount += 1
					if printY + 1 != 50:
						if (run[printY + 1][printX] == 1) or (run[printY + 1][printX] == 2):
							sqcount += 1
					if printY - 1 != -1:
						if (run[printY - 1][printX] == 1) or (run[printY - 1][printX] == 2):
							sqcount += 1
					if printX + 1 != 50 and printY + 1 != 50:
						if (run[printY + 1][printX + 1] == 1) or (run[printY + 1][printX + 1] == 2):
							sqcount += 1
					if printX - 1 != -1 and printY + 1 != 50:
						if (run[printY + 1][printX - 1] == 1) or (run[printY + 1][printX - 1] == 2):
							sqcount += 1
					if printX + 1 != 50 and printY - 1 != -1:
						if (run[printY - 1][printX + 1] == 1) or (run[printY - 1][printX + 1] == 2):
							sqcount += 1
					if printX - 1 != -1 and printY - 1 != -1:
						if (run[printY - 1][printX - 1] == 1) or (run[printY - 1][printX - 1] == 2):
							sqcount += 1

					if sq == 1:
						if sqcount < 2 or sqcount > 3:
							run[printY][printX] = 2

					if sq == 0:
						if sqcount == 3:
							run[printY][printX] = 3

					printX += 1
				printY += 1
				printX = 0

			printX = 0
			printY = 0
			for row in run:
				for sq in row:
					if sq == 2:
						run[printY][printX] = 0
					if sq == 3:
						run[printY][printX] = 1
					printX += 1
				printY += 1
				printX = 0

		if lClick == False:
			tick = False

	draw()
	pygame.display.update()

with open('save.txt', 'w') as data:
	for row in save:
		data.write(' '.join(map(str, row)) + '\n')